import React, { useState, useEffect } from 'react';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';
import './css/chatPage.css';


function ChatPage({ username, onLogout }) {
    const [stompClient, setStompClient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
  
    useEffect(() => {
      const socket = new SockJS('http://localhost:8080/chat-socket');
      const stompClient = Stomp.over(socket);
  
      stompClient.connect({}, () => {
        stompClient.subscribe('/topic/messages', (message) => {
          setMessages(messages => [...messages, JSON.parse(message.body)]);
        });
      });
  
      setStompClient(stompClient);
      return () => {
        stompClient.disconnect();
      };
    }, []);
  
    const sendMessage = () => {
      if (newMessage && stompClient) {
        const chatMessage = { sender: username, content: newMessage };
        stompClient.send("/app/chat/send", {}, JSON.stringify(chatMessage));
        setNewMessage('');
      }
    };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Daegyo Talk</h1>
        <div className="chat-header-right">
          <span>{username}님 환영합니다.</span>
          <button onClick={onLogout}>로그아웃</button>
        </div>
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className="message-bubble">
            <div className="message-info">
              <span className="message-sender">{msg.sender}</span>
              <div className="message-text">{msg.content}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="message-input">
        <input 
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="메시지 입력"
        />
        <button onClick={sendMessage}>전송</button>
      </div>
    </div>
  );
}

export default ChatPage;
