import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './css/chatPage.css';

function ChatPage({ username, onLogout }) {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // 서버와의 WebSocket 연결을 설정합니다.
    const newSocket = io('http://localhost:8090'); // 서버 URL을 맞게 수정하세요.
    setSocket(newSocket);

    // 서버로부터 메시지를 받으면 상태에 추가합니다.
    newSocket.on('chat message', (msg) => {
      setMessages(messages => [...messages, msg]);
    });

    // 컴포넌트가 언마운트될 때 연결을 닫습니다.
    return () => {
      if (newSocket) newSocket.close();
    };
  }, []);

  const sendMessage = () => {
    // 메시지 전송 로직
    if (socket && newMessage.trim()) {
      const message = {
        sender: username,
        text: newMessage,
        timestamp: new Date().toISOString() // ISO 형식의 타임스탬프
      };

      socket.emit('chat message', message);
      setNewMessage('');
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Daegyo Talk</h1>
        <div className="chat-header-right">
          <span>{username}님 환영합니다.</span>
          <button onClick={onLogout}>로그아웃</button>
        </div>
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className="message-bubble">
            <div className="message-info">
              <span className="message-sender">{msg.sender}</span>
              <span className="message-time">{new Date(msg.timestamp).toLocaleTimeString()}</span>
            </div>
            <div className="message-text">{msg.text}</div>
          </div>
        ))}
      </div>
      <div className="message-input">
        <input 
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="메시지 입력"
          onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={sendMessage}>전송</button>
      </div>
    </div>
  );
}

export default ChatPage;
